mapper.select:
 		select i.*,r.ad_position,b.behavior,cls.cname,dic.dic_value from ad_info i 
 		left join ad_display_rule r on r.id = i.ad_rule_id 
 		left join ad_behavior b on b.id=i.ad_behavior_id 
 		left join ad_classify cls on cls.cid = i.ad_classify_id 
 		left join sys_dictionary dic on dic.id = i.billing_type 
 		where 1=1 

      	and i.ad_rule_id=#{ai.adRuleId} 


		    and i.cp_name like CONCAT('%','${ai.cpName}','%' )


 		    and i.ad_behavior_id = #{ai.adBehaviorId} 


 		    and i.ad_classify_id = #{ai.adClassifyId} 


 		    and i.start_time >= #{ai.startTime} 


 		    and i.end_time <= #{ai.endTime} 

 		 limit #{startIndex},#{numPerPage}

    select count(1) from ad_info i where 1=1 

